    }

    console.log(tasks);

    return (
        <div className="App">
            <h1>TaskApp</h1>
            <NewTaskForm task={task} setTask={setTask} addTask={addTask} />
            <TaskList tasks={tasks} setTasks={setTasks} />
        </div>
    );
}

export default App;
