import { useEffect, useState } from 'react';
import { Task } from '../model';
import { TaskItem } from './TaskItem';
import axios from 'axios';

interface Props {
    tasks: Task[];
    setTasks: React.Dispatch<React.SetStateAction<Task[]>>;
    toggleCompleted: (id: string, completed: boolean) => void;
    deleteTask: (id: string) => void;
    editTask: (id: string, newTitle: string) => void;
}

export function TaskList({ toggleCompleted, deleteTask, editTask }: Props) {
    const [task, setTask] = useState("");
    const [tasks, setTasks] = useState<Task[]>([]);

    useEffect(() => {
        axios.get(`localhost:8081/api/v1/tasks`)
            .then(res => {
                setTasks(res.data);
            });
    }, []);

    return (
        <div className="tasks">
            <ul>
                {tasks.map((task) => {
                    return <TaskItem {...task} key={task.id} toggleCompleted={toggleCompleted} deleteTask={deleteTask} editTask={editTask} />;
                })}
            </ul>
        </div>
    );
}
