import { useState } from 'react';

export function InputField({ addTask }) {
    const [task, setTask] = useState("");

    function handleSubmit(e: any) {
        e.preventDefault();
        if (task === "") return;
        addTask(task);
        setTask("");
    }

    console.log(task);
    return (
    <form onSubmit={handleSubmit}>
        <input
            value={task}
            onChange={(e) => setTask(e.target.value)}
            type="text"
            placeholder='Add a new task...'
        />
        <button>Add</button>
    </form>
    )
}
