import { useState } from 'react';
import './App.css';
import { NewTaskForm } from './components/NewTaskForm';
import { TaskList } from './components/TaskList';
import { Task } from './model';

function App() {
    const [task, setTask] = useState("");
    const [tasks, setTasks] = useState<Task[]>([]);

    function addTask(e: React.FormEvent) {
        setTasks([...tasks, { id: crypto.randomUUID(), title: task, completed: false }]);
    }

    function deleteTask(id: string) {
        setTasks((currentTasks) => {
           return currentTasks.filter((task) => task.id !== id); 
        })
    }

  function deleteTodo(id: string) {
    setTodos((currentTodos) => {
      return currentTodos.filter((todo) => todo.id !== id);
    });
  }
    function toggleCompleted(id: string, completed: boolean) {
        setTasks((currentTasks) => {
            return currentTasks.map((task) => {
                if (task.id === id) {
                    return { ...task, completed };
                }
                return task;
            });
        });
    }
    console.log(tasks);

    return (
        <div className="App">
            <h1>TaskApp</h1>
            <NewTaskForm task={task} setTask={setTask} addTask={addTask} />
            <TaskList tasks={tasks} setTasks={setTasks} toggleCompleted={toggleCompleted} />
        </div>
    );
}

export default App;
