import { MdDelete } from 'react-icons';

interface Props {
    id: string;
    title: string;
    completed: boolean;
    toggleCompleted: (id: string, completed: boolean) => void;
    deleteTask: (id: string) => void;
}

export function TaskItem({ id, title, completed, toggleCompleted, deleteTask }: Props) {

    return (
        <div>
            <li>
                <input
                    type="checkbox"
                    checked={completed}
                    onChange={(e) => toggleCompleted(id, e.target.checked)} />
                {title}
                <button>Edit</button>
                <button onClick={() => deleteTask(id)}>Delete</button>
            </li>
        </div>
    );
}
