import { MdDelete } from 'react-icons/md';
import { useState } from 'react';
import { MdEdit } from 'react-icons/md';

interface Props {
    id: string;
    title: string;
    completed: boolean;
    toggleCompleted: (id: string, completed: boolean) => void;
    deleteTask: (id: string) => void;
    editTask: (id: string, newTitle: string) => void;
}

export function TaskItem({ id, title, completed, toggleCompleted, deleteTask, editTask }: Props) {

    const [editing, setEditing] = useState(false);

    function toggleEditing(e: React.KeyboardEvent<HTMLInputElement>) {
        if (e.key === 'Enter') {
            setEditing(true);
            return;
        }
        if (editing) setEditing(false);
        else setEditing(true);
    }

    console.log(editing);
    return (
        <div>
            <li>
                <input
                    type="checkbox"
                    checked={completed}
                    onChange={(e) => toggleCompleted(id, e.target.checked)} />
                {title}
                <MdEdit onClick={(e) => toggleEditing(e.target.value)} />
                <MdDelete onClick={() => deleteTask(id)} />
                <input type="text" value={title} onChange={(e) => editTask(id, e.target.value)} onKeyPress={(e) => toggleEditing(e.target.value)} />
            </li>
        </div>
    );
}
