import { MdDelete } from 'react-icons/md';
import CSS from 'csstype';
import { useState } from 'react';
import { MdEdit } from 'react-icons/md';

interface Props {
    id: string;
    title: string;
    completed: boolean;
    toggleCompleted: (id: string, completed: boolean) => void;
    deleteTask: (id: string) => void;
    editTask: (id: string, newTitle: string) => void;
}

export function TaskItem({ id, title, completed, toggleCompleted, deleteTask, editTask }: Props) {

    const [editing, setEditing] = useState(false);

    let viewMode: CSS.Properties = {};
    let editMode: CSS.Properties = {};

    (editing) ? viewMode.display = 'none' : editMode.display = 'none';

    function toggleEditing() {
        if (editing) {
            setEditing(false);
        } else {
            setEditing(true);
        }
    }

    console.log(editing);
    return (
        <div>
            <li>
                <div className="view-task" style={viewMode}>
                    <input
                        style={{textDecoration: "line-through"}}
                        type="checkbox"
                        checked={completed}
                        onChange={(e) => toggleCompleted(id, e.target.checked)} />
                    {title}
                    <MdEdit onClick={() => toggleEditing()} />
                    <MdDelete onClick={() => deleteTask(id)} />
                </div>
                <input
                    style={editMode}
                    type="text"
                    value={title}
                    onChange={(e) => editTask(id, e.target.value)}
                    onKeyUp={(e) => { if (e.key === 'Enter') toggleEditing() }} />
            </li>
        </div>
    );
}
