import { Task } from '../model';
import { TaskItem } from './TaskItem';

interface Props {
    tasks: Task[];
    setTasks: React.Dispatch<React.SetStateAction<Task[]>>;
    toggleCompleted: (id: string, completed: boolean) => void;
    deleteTask: (id: string) => void; 
    editTask: (id: string, newTitle: string) => void; 
    toggleEditing: VoidFunction;
}

export function TaskList({ tasks, setTasks, toggleCompleted, deleteTask, editTask, toggleEditing }: Props) {

    return (
        <ul>
            {tasks.map((task) => {
                return <TaskItem {...task} key={task.id} toggleCompleted={toggleCompleted} deleteTask={deleteTask} editTask={editTask} toggleEditing={toggleEditing}/>;
            })}
        </ul>
    );
}
