import { MdDelete } from 'react-icons/md';
import { MdEdit } from 'react-icons/md';

interface Props {
    id: string;
    title: string;
    completed: boolean;
    toggleCompleted: (id: string, completed: boolean) => void;
    deleteTask: (id: string) => void;
    editTask: (id: string, newTitle: string) => void;
    toggleEditing: VoidFunction;
}

export function TaskItem({ id, title, completed, toggleCompleted, deleteTask, editTask, toggleEditing }: Props) {

    return (
        <div>
            <li>
                <input
                    type="checkbox"
                    checked={completed}
                    onChange={(e) => toggleCompleted(id, e.target.checked)} />
                {title}
                <MdEdit onClick={() => editTask(id, title)} />
                <MdDelete onClick={() => deleteTask(id)} />
                <input type="text" value={title} onChange={(e) => editTask(id, e.target.value)} />
            </li>
        </div>
    );
}
