import { Task } from '../model';
import { TaskItem } from './TaskItem';

interface Props {
    tasks: Task[];
    setTasks: React.Dispatch<React.SetStateAction<Task[]>>;
    toggleCompleted: (id: string, completed: boolean) => void;
    deleteTask: (id: string) => void;
    editTask: (id: string, newTitle: string) => void;
}


export function TaskList({tasks, setTasks, toggleCompleted, deleteTask, editTask }: Props) {
    return (
        <div className="tasks">
            <ul>
                {tasks.map((task) => {
                    <TaskItem {...task} key={task.id} toggleCompleted={toggleCompleted} deleteTask={deleteTask} editTask={editTask} />;
                })}
            </ul>
        </div>
    );
}
